 int val;\n *     ListNode next;\n *     ListNode(int x) { val = x; }\n * }\n */\nclass Solution {\n    public ListNode oddEvenList(ListNode head) {\n        \n    }\n}\" },{\"value\": \"python\", \"text\": \"Python\", \"defaultCode\": \"# Definition for singly-linked list.\r\n# class ListNode(object):\r\n#     def __init__(self, x):\r\n#         self.val = x\r\n#         self.next = None\r\n\r\nclass Solution(object):\r\n    def oddEvenList(self, head):\r\n        """\r\n        :type head: ListNode\r\n        :rtype: ListNode\r\n        """\r\n        \" },{\"value\": \"c\", \"text\": \"C\", \"defaultCode\": \"/**\r\n * Definition for singly-linked list.\r\n * struct ListNode {\r\n *     int val;\r\n *     struct ListNode *next;\r\n * };\r\n */\r\nstruct ListNode* oddEvenList(struct ListNode* head) {\r\n    \r\n}\" },{\"value\": \"csharp\", \"text\": \"C#\", \"defaultCode\": \"/**\r\n * Definition for singly-linked list.\r\n * public class ListNode {\r\n *     public int val;\r\n *     public ListNode next;\r\n *     public ListNode(int x) { val = x; }\r\n * }\r\n */\r\npublic class Solution {\r\n    public ListNode OddEvenList(ListNode head) {\r\n        \r\n    }\r\n}\" },{\"value\": \"javascript\", \"text\": \"JavaScript\", \"defaultCode\": \"/**\r\n * Definition for singly-linked list.\r\n * function ListNode(val) {\r\n *     this.val = val;\r\n *     this.next = null;\r\n * }\r\n */\r\n/**\r\n * @param {ListNode} head\r\n * @return {ListNode}\r\n */\r\nvar oddEvenList = function(head) {\r\n    \r\n};\" },{\"value\": \"ruby\", \"text\": \"Ruby\", \"defaultCode\": \"# Definition for singly-linked list.\r\n# class ListNode\r\n#     attr_accessor :val, :next\r\n#     def initialize(val)\r\n#         @val = val\r\n#         @next = nil\r\n#     end\r\n# end\r\n\r\n# @param {ListNode} head\r\n# @return {ListNode}\r\ndef odd_even_list(head)\r\n    \r\nend\" },{\"value\": \"swift\", \"text\": \"Swift\", \"defaultCode\": \"/**\n * Definition for singly-linked list.\n 